mapper.update:
update person set name = #{name}, gender = #{gender}, age = #{age}
                , phone = #{phone}, id_card = #{idCard}, role_type = #{roleType},
                  position = #{position} , update_time = #{updateTime} where id = #{id}
and role_type=2

mapper.delete:
# 批量删除教师 delete from person where id in
#{id}
and role_type = 2
